import React, { useState } from "react";
import MenuItemWithDropdown from "./MenuItemWithDropdown";
import './Menu.css'

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);

  const menu = [
    {
      title: "Sobre nós",
      dropdownItems: ["Quem somos", "Por que visitar", "Regulamento", "Política de Privacidade"],
    },
    {
      title: "Visitação",
      dropdownItems: ["Mapa da feira", "Hotéis", "Restaurantes", "Pontos turísticos"],
    },
    {
      title: "Programação",
      dropdownItems: ["Palestras", "Workshops", "Oficinas", "Shows"],
    },

  ]

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  return (
    <ul className="ul-menu">
      <MenuItemWithDropdown
        index={0}
        title="Sobre nós"
        dropdownItems={["Quem somos", "Por que visitar", "Regulamento", "Política de Privacidade"]}
        isActive={activeDropdown === 0}
        onDropdownClick={() => handleDropdownClick(0)}
      />
      <MenuItemWithDropdown
        index={1}
        title="Visitação"
        dropdownItems={["Mapa da feira", "Hotéis", "Restaurantes", "Pontos turísticos"]}
        isActive={activeDropdown === 1}
        onDropdownClick={() => handleDropdownClick(1)}
      />
      <MenuItemWithDropdown
        index={2}
        title="Programação"
        dropdownItems={["Palestras", "Workshops", "Oficinas", "Shows"]}
        isActive={activeDropdown === 2}
        onDropdownClick={() => handleDropdownClick(2)}
      />
      <MenuItemWithDropdown
        index={3}
        title="Expositores"  
      />
      <MenuItemWithDropdown
        index={4}
        title="Parceiros"
      />
    </ul>
  );
};

export default Menu;
