import React from 'react'
import Menu from '../menus/Menu'
import './Header.css'
import Logo from './Logo'
import imgDony from '../../image/donys-3.png';
import { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const NumeroAnimado = ({ numero }) => {
  const [visible, setVisible] = useState(false);
  const props = useSpring({ number: visible ? numero : 0, from: { number: 0 } });

  return (
    <animated.h4 className="numero" onMouseEnter={() => setVisible(true)}>
      {props.number.interpolate((num) => Math.floor(num))}
    </animated.h4>
  );
};

const Header = () => {
  return (
    <div className='page-01'>
      <div className='div-logo-number'>
           <Logo /> 
           <div className='numbers'>
              <div className='num-p'>
                  <NumeroAnimado numero={70} />
                  <p>Expositores e Marcas</p>
              </div>
              <div className='num-p'>
                  <NumeroAnimado numero={3500} />
                  <p>Expectativas de visitantes</p>
              </div>
              <div className='num-p'>
                  <NumeroAnimado numero={900000000} />
                  <p>Expositores</p>
              </div>    
           </div>
      </div>

      <div>
          <Menu />
      </div>
      <div className='text-img'>
        <div className='text-palestra'>
          <h1>PALESTRA CONFIRMADA!</h1>
          <p>Não perca a oportunidade de aprender com Dony De Nuccio, renomado profissional 
          na área financeira e empresarial, durante sua palestra. 
           Garanta seu lugar agora mesmo e aproveite essa chance de networking!</p>

           <button>Adquira o seu ingresso</button>
        </div>
        <div className='imagens'>
          <img src={imgDony} alt='Dony' className='imgDony'/>
        </div>

      </div>


    </div>
  );
};

export default Header;
