import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import "./Menu.css";

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);

  const menu = [
    {
      section: "sobre-nos",
      label: "Sobre Nós",
    },
    {
      section: "expositores",
      label: "Expositores",
    },
    {
      section: "parceiros",
      label: "Parceiros",
    },
    {
      title: "Programação",
      dropdownItems: [
        { label: "Palestras", path: "/palestras" },
        { label: "Workshops", path: "/workshops" },
        { label: "Oficinas", path: "/oficinas" },
        { label: "Shows e Performance", path: "/shows" },
      ],
    },
  ];

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  function handleLinkClick() {
    setActiveDropdown(null);
  }

  function handleScroll(sectionId) {
    const section = document.getElementById(sectionId);
    section.scrollIntoView({ behavior: "smooth" });
    setActiveDropdown(null);
  }

  return (
    <div>
      <ul className="ul-menu">
        <li>
          <div className="dropdown">
            <button className="dropdown-toggle" onClick={() => handleDropdownClick(menu.length - 1)}>
              Programação
            </button>
            {activeDropdown === menu.length - 1 && (
              <ul className="dropdown-menu">
                {menu[menu.length - 1].dropdownItems.map((item, index) => (
                  <li key={index}>
                    <NavLink
                      className="dropdown-item"
                      to={item.path}
                      activeClassName="active"
                      onClick={handleLinkClick}
                    >
                      {item.label}
                    </NavLink>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </li>
        {menu.slice(0, menu.length - 1).map((item, index) => (
          <li key={index}>
            <button className="li-button" onClick={() => handleScroll(item.section)}>
              {item.label}
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Menu;
