import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import "./Menu.css";

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const menu = [
    {
      section: "sobre-nos",
    },
    {
      title: "Visitação",
      type: "link",
      link: "/",
    },
    {
      title: "Programação",
      dropdownItems: [
        { label: "Palestras", path: "/palestras" },
        { label: "Workshops", path: "/workshops" },
        { label: "Oficinas", path: "/oficinas" },
        { label: "Shows e Performance", path: "/shows" },
      ],
    },
    {
      section: "expositores",
    },
    {
      section: "parceiros",
    },
  ];

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  function handleLinkClick() {
    setActiveDropdown(null);
    setMenuOpen(false);
  }

  function handleMenuIconClick() {
    setMenuOpen(!menuOpen);
  }

  function handleScroll(sectionId) {
    const section = document.getElementById(sectionId);
    section.scrollIntoView({ behavior: "smooth" });
  }

  return (
    <div>
      <div className="menu-icon-div">
        <button className="menu-icon" onClick={handleMenuIconClick}>
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
      <ul className={`ul-menu ${menuOpen ? "show" : ""}`}>
        <div className="div-li-buttons">
          {menu && menu.map((item, index) => {
            if (item.section) {
              return (
                <li key={index} className="li-section">{item.section}</li>
              );
            } else {
              return (
                <li key={index} className="li-dropdown">
                  <button
                    className="li-button"
                    onClick={() => handleDropdownClick(index)}
                  >
                    {item.title}
                  </button>
                  {activeDropdown === index && item.dropdownItems && (
                    <ul className="dropdown-menu">
                      {item.dropdownItems.map((dropdownItem, i) => (
                        <li key={i} onClick={handleLinkClick}>
                          <Link to={dropdownItem.path}>{dropdownItem.label}</Link>
                        </li>
                      ))}
                    </ul>
                  )}
                </li>
              );
            }
          })}
        </div>
      </ul>
      <div className="menu-overlay" onClick={handleMenuIconClick}></div>
    </div>
  );
};
