import React from "react";
import { useHistory } from "react-router-dom";
import "./MenuItemWithDropdown.css";

function MenuItemWithDropdown({
  title,
  dropdownItems = [],
  isActive,
  onDropdownClick,
  type = "button",
  link,
}) {
  const history = useHistory();

  function handleClick() {
    if (type === "link") {
      if (link === "/expositores") {
        history.push("/expositores");
      } else {
        window.location.href = link;
      }
    } else if (onDropdownClick) {
      onDropdownClick();
    }
  }

  return (
    <li className="li-menu">
      <button className={isActive ? "button-active" : ""} onClick={handleClick}>
        {title}
      </button>
      {dropdownItems && isActive && (
        <ul className="ul-dropdown">
          {dropdownItems.map((item) => (
            <li key={item}>{item}</li>
          ))}
        </ul>
      )}
    </li>
  );
}

export default MenuItemWithDropdown;
