import React from "react";
import Menu from "../menus/Menu";
import "./Header.css";
import Logo from "./Logo";
import imgDony from "../../image/donys-3.png";
import { useSpring } from "react-spring";

const Header = () => {
  const animacaoNumero = (valorFinal) => {
    return useSpring({
      from: { number: 1 },
      to: { number: valorFinal },
      config: { duration: 2000, clamp: true },
    });
  };

  return (
    <div className="page-01">
      <div className="div-logo-number">
        <Logo />
        <div className="numbers">
          <div className="num-p">
            <animated.h4 className="numero">
              {animacaoNumero(70).number}
            </animated.h4>
            <p>Expositores e Marcas</p>
          </div>
          <div className="num-p">
            <animated.h4 className="numero">
              {animacaoNumero(3500).number}
            </animated.h4>
            <p>Expectativas de visitantes</p>
          </div>
          <div className="num-p">
            <animated.h4 className="numero">
              {animacaoNumero(900000000).number}
            </animated.h4>
            <p>Expositores</p>
          </div>
        </div>
      </div>

      <div>
        <Menu />
      </div>
      <div className="text-img">
        <div className="text-palestra">
          <h1>PALESTRA CONFIRMADA!</h1>
          <p>
            Não perca a oportunidade de aprender com Dony De Nuccio, renomado
            profissional na área financeira e empresarial, durante sua palestra.
            Garanta seu lugar agora mesmo e aproveite essa chance de networking!
          </p>

          <button>Adquira o seu ingresso</button>
        </div>
        <div className="imagens">
          <img src={imgDony} alt="Dony" className="imgDony" />
        </div>
      </div>
    </div>
  );
};

export default Header;
