import React from "react";
import './MenuItemWithDropdown.css';

function MenuItemWithDropdown({ title, dropdownItems, isActive, onDropdownClick, component }) {
  function handleMenuItemClick() {
    if (component) {
      history.push(`/${component}`);
    }
  }

  return (
    <li className="li-button">
      <button onClick={() => {
        onDropdownClick();
        handleMenuItemClick();
      }}>{title}</button>
      {isActive && (
        <ul className="ul-dropdown">
          {dropdownItems.map((item) => (
            <li key={item}>{item}</li>
          ))}
        </ul>
      )}
    </li>
  );
}

export default MenuItemWithDropdown;
