import React, { useState } from "react";
import MenuItemWithDropdown from "./MenuItemWithDropdown";
import { Link, NavLink } from "react-router-dom";
import "./Menu.css";

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);

  const menu = [
    {
      title: "Sobre nós",
      dropdownItems: [
        { link: "Quem somos", path: "/quem-somos" },
        { link: "Por que visitar", path: "/por-que-visitar" },
        { link: "Regulamento", path: "/regulamento" },
        { link: "Política de Privacidade", path: "/politica-de-privacidade" },
      ],
    },
    {
      title: "Visitação",
      dropdownItems: [
        { link: "Mapa da feira", path: "/mapa-da-feira" },
        { link: "Hotéis", path: "/hoteis" },
        { link: "Restaurantes", path: "/restaurantes" },
        { link: "Pontos turísticos", path: "/pontos-turisticos" },
      ],
    },
    {
      title: "Programação",
      dropdownItems: [
        { link: "Palestras", path: "/palestras" },
        { link: "Workshops", path: "/workshops" },
        { link: "Oficinas", path: "/oficinas" },
        { link: "Shows", path: "/shows" },
      ],
    },
    {
      title: "Expositores",
      link: "/exp",
      type: "link",
    },
    {
      title: "Parceiros",
      link: "/part",
      type: "link",
    },
  ];

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  function handleLinkClick() {
    setActiveDropdown(null);
  }

  return (
    <ul className="ul-menu">
      {menu.map((item, index) => (
        <MenuItemWithDropdown
          key={index}
          title={item.title}
          dropdownItems={item.dropdownItems}
          isActive={activeDropdown === index}
          onDropdownClick={() => handleDropdownClick(index)}
          type={item.type}
          link={item.link}
          onClick={handleLinkClick}
        >
          {item.to ? (
            <Link to={item.link} onClick={handleLinkClick}>
              {item.title}
            </Link>
          ) : (
            <button onClick={() => handleDropdownClick(index)}>{item.title}</button>
          )}
          {item.dropdownItems && (
            <ul className="dropdown-menu">
              {item.dropdownItems.map((dropdownItem, dropdownIndex) => (
                <li key={dropdownIndex}>
                  <NavLink
                    to={dropdownItem.path}
                    activeClassName="active"
                    onClick={handleLinkClick}
                  >
                    {dropdownItem.label}
                  </NavLink>
                </li>
              ))}
            </ul>
          )}
        </MenuItemWithDropdown>
      ))}
    </ul>
  );
};

export default Menu;
