import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import "./Menu.css";

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const menu = [
    {
      section: "sobre-nos",
    },
    {
      title: "Visitação",
      type: "link",
      link: "/",
    },
    {
      title: "Programação",
      dropdownItems: [
        { label: "Palestras", path: "/palestras" },
        { label: "Workshops", path: "/workshops" },
        { label: "Oficinas", path: "/oficinas" },
        { label: "Shows e Performance", path: "/shows" },
      ],
    },
    {
      section: "expositores",
    },
    {
      section: "parceiros",
    },
  ];

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  function handleLinkClick() {
    setActiveDropdown(null);
    setMenuOpen(false);
  }

  function handleScroll(sectionId) {
    const section = document.getElementById(sectionId);
    section.scrollIntoView({ behavior: "smooth" });
  }

  return (
    <div>

      <ul className={`ul-menu ${menuOpen ? "show" : ""}`}>
      <div className="div-li-buttons">
      <li>
          <button
            className="li-button"
            onClick={() => handleScroll("sobre-nos")}
          >
            Sobre Nós
          </button>
        </li>
        <li>
          <button
            className="li-button"
            onClick={() => handleScroll("expositores")}
          >

            Expositores
          </button>
        </li>
        <li>       
          <button
            className="li-button"
            onClick={() => handleScroll("parceiros")}
          >
            Parceiros
          </button>
        </li>
      </div>
        {menu.map((item, index) =>
          item.type === "link" ? (
            <li className="li-link" key={index}>
              <Link
                to={item.link}
                className="li-links-title"
                onClick={handleLinkClick}
              >
                {item.title}
              </Link>
            </li>
          ) : (
            <li key={index}>
              <button
                className="li-button-drop"
                onClick={() => handleDropdownClick(index)}
              >
                {item.title}
              </button>
              {activeDropdown === index && (
                <ul className="dropdown-menu show">
                  {item.dropdownItems.map((dropdownItem, dropdownIndex) => (
                    <li key={dropdownIndex}>
                      <NavLink
                        className="li-link-dropdown"
                        to={dropdownItem.path}
                        activeClassName="active"
                        onClick={handleLinkClick}
                      >
                        {dropdownItem.label}
                      </NavLink>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          )
        )}

      </ul>
    </div>

  );
};

export default Menu;
