import React, { useState } from "react";
import Expositores from "../expositores/Expositores";
import MenuItemWithDropdown from "./MenuItemWithDropdown";
import "./Menu.css";

const menuData = [
  {
    index: 0,
    title: "Sobre nós",
    dropdownItems: [
      "Quem somos",
      "Por que visitar",
      "Regulamento",
      "Política de Privacidade",
    ],
  },
  {
    index: 1,
    title: "Visitação",
    dropdownItems: [
      "Mapa da feira",
      "Hotéis",
      "Restaurantes",
      "Pontos turísticos",
    ],
  },
  {
    index: 2,
    title: "Programação",
    dropdownItems: ["Palestras", "Workshops", "Oficinas", "Shows"],
  },
  {
    index: 3,
    title: "Expositores",
    component: <Expositores />,
  },
  {
    index: 4,
    title: "Parceiros",
  },
];

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  return (
    <ul className="ul-menu">
      {menuData.map((item) => (
        <MenuItemWithDropdown
          key={item.index}
          index={item.index}
          title={item.title}
          dropdownItems={item.dropdownItems}
          component={item.component}
          isActive={activeDropdown === item.index}
          onDropdownClick={() => handleDropdownClick(item.index)}
        />
      ))}
    </ul>
  );
};

export default Menu;
