import React, { useState } from "react";
import { Link, NavLink } from "react-router-dom";
import "./Menu.css";

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);

  const menu = [
    {
      section: "sobre-nos",
      isDropdown: false // novo campo
    },
    {
      title: "Visitação",
      type: "link",
      link: "/",
      isDropdown: false // novo campo
    },
    {
      title: "Programação",
      dropdownItems: [
        { label: "Palestras", path: "/palestras" },
        { label: "Workshops", path: "/workshops" },
        { label: "Oficinas", path: "/oficinas" },
        { label: "Shows e Performance", path: "/shows" },
      ],
      isDropdown: true // novo campo
    },
    {
      section: "expositores",
      isDropdown: false // novo campo
    },
    {
      section: "parceiros",
      isDropdown: false // novo campo
    },
  ];

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  function handleLinkClick() {
    setActiveDropdown(null);
    setMenuOpen(false);
  }

  function handleMenuIconClick() {
    setMenuOpen(!menuOpen);
  }

  function handleScroll(sectionId) {
    const section = document.getElementById(sectionId);
    section.scrollIntoView({ behavior: "smooth" });
  }

  // nova função
  function handleNonDropdownClick() {
    setActiveDropdown(null);
  }

  return (
    <div>
      <div className="menu-icon-div">
        <button className="menu-icon" onClick={handleMenuIconClick}>
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
      <ul className={`ul-menu ${menuOpen ? "show" : ""} div-li-buttons`}>
        <li>
          <button
            className="li-button"
            onClick={() => { handleScroll("sobre-nos"); handleNonDropdownClick(); }} // chama a nova função
          >
            Sobre Nós
          </button>
        </li>
        <li>
          <button
            className="li-button"
            onClick={() => { handleScroll("expositores"); handleNonDropdownClick(); }} // chama a nova função
          >
            Expositores
          </button>
        </li>
        <li>       
          <button
            className="li-button"
            onClick={() => { handleScroll("parceiros"); handleNonDropdownClick(); }} // chama a nova função
          >
            Parceiros
          </button>
        </li>
        {menu.map((item, index) =>
          item.type === "link" ? (
            <li className="li-link" key={index}>
              <Link
                to={item.link}
                className="li-links-title"
                onClick={handleLinkClick}
              >
                {item.title}
</Link>
</li>
) : (
<li key={index}>
<button
className="li-button-drop"
onClick={() => handleDropdownClick(index)}
>
{item.title}
</button>
{activeDropdown === index && (
<ul className="dropdown-menu show">
{item.dropdownItems.map((dropdownItem, dropdownIndex) => (
<li key={dropdownIndex}>
<NavLink
className="li-link-dropdown"
to={dropdownItem.path}
activeClassName="active"
onClick={() => { handleLinkClick(); handleNonDropdownClick(); }} // chama a nova função
>
{dropdownItem.label}
</NavLink>
</li>
))}
</ul>
)}
</li>
)
)}
</ul>
</div>
);

};

export default Menu;
