import React, { useState } from "react";
import MenuItemWithDropdown from "./MenuItemWithDropdown";
import { Link } from "react-router-dom";
import './Menu.css'

const Menu = () => {
  const [activeDropdown, setActiveDropdown] = useState(null);

  const menu = [
    {
      title: "Sobre nós",
      dropdownItems: ["Quem somos", "Por que visitar", "Regulamento", "Política de Privacidade"],
    },
    {
      title: "Visitação",
      dropdownItems: ["Mapa da feira", "Hotéis", "Restaurantes", "Pontos turísticos"],
    },
    {
      title: "Programação",
      dropdownItems: ["Palestras", "Workshops", "Oficinas", "Shows"],
    },
    {
      title: "Expositores",
      link: "/exp"
    },
    {
      title: "Parceiros",
      link: "/part"
    }
  ]

  function handleDropdownClick(index) {
    setActiveDropdown(activeDropdown === index ? null : index);
  }

  return (
    <ul className="ul-menu">
    {menu.map((item, index) => (
      <MenuItemWithDropdown
        key={index}
        title={item.title}
        dropdownItems={item.dropdownItems}
        isActive={activeDropdown === index}
        onDropdownClick={() => handleDropdownClick(index)}
      >
        {item.to ? (
          <Link link={item.to}>{item.title}</Link>
        ) : (
          <button onClick={() => handleDropdownClick(index)}>{item.title}</button>
        )}
      </MenuItemWithDropdown>
    ))}
  </ul>
  );
};

export default Menu;
